edit.find:
// Handle keys
			if ($invite->config['enable'])
			{
				if ($invite->config['auth_key'] == 1)
				{
					if (!$invite->valid_key($data['key']) || empty($data['key']))
					{
						$error[] 	= $user->lang['AUTH_KEY_WRONG'];
					}
				}
				elseif ($invite->config['auth_key'] == 2)
				{
					if (!$invite->valid_key($data['key']))
					{
						$error[] 	= $user->lang['OPTIONAL_AUTH_KEY_WRONG'];
					}
				}
			}
			if ($invite->self_invite_check($data['key']))
			{
				$error[] 	= $user->lang['INVITE_YOURSELF'];
			}
