// Calculating colspans in php files is not cricket at all and I have to find a better solution... 
$table_colspan = array('prosilver', 'subsilver2');
$prosilver = ($auth->acl_get('u_viewonline')) ? 5 : 4;
$subsilver2 = (($form && $field) ? true : false) ? 9 : 8;

foreach ($table_colspan as $k => $v)
{
	$increase = ($k == 'prosilver') ? $prosilver : $subsilver2;
	$increase+= ($invitation->config['display_m_inviter']) ? 1 : 0;
	$increase+= ($invitation->config['display_m_invite']) ? 1 : 0;
	$increase+= ($invitation->config['display_m_register']) ? 1 : 0;

	if ($k == 'prosilver')
	{
		$prosilver = $increase;
	}
	else
	{
		$subsilver2 = $increase;
	}
}

$template->assign_vars(array(
	'S_TABLE_COLSPAN'			=> $prosilver,
	'S_TABLE_COLSPAN_SUBSILVER'	=> $subsilver2,

