$l_reg_cond = '';
switch ($config['require_activation'])
{
	case USER_ACTIVATION_SELF:
		$l_reg_cond = $user->lang['UCP_EMAIL_ACTIVATE'];
	break;

	case USER_ACTIVATION_ADMIN:
		$l_reg_cond = $user->lang['UCP_ADMIN_ACTIVATE'];
	break;
}
