				if ($invite->config['enable_key'] == 1)
				{
					if (!$invite->valid_key($register_key) || empty($register_key))
					{
						$error[] 	= $user->lang['REGISTER_KEY_INVALID'];
					}
				}
				elseif ($invite->config['enable_key'] == 2)
